class LoadingUI extends egret.Sprite {
    //Logo图片
    private static logoBase64:string = "data:image/png;base64,";
    //进度条背景
    private static progressBGBase64:string = "data:image/png;base64,";
    //进度条
    private static progressBase64:string = "data:image/png;base64,";
    private logo:egret.Bitmap = null;
    private progressBG:egret.Bitmap = null;
    private progress:egret.Bitmap = null;
    private progressText:egret.TextField = new egret.TextField();
    private loadCount:number = 3;

    public constructor() {
        super();
        var loadComplete:Function = function (event:egret.Event) {
            var loader:egret.URLLoader = <egret.URLLoader>event.target;
            var texture:egret.Texture = loader.data;
            if (loader["tag"] == "logo") {
                this.logo = new egret.Bitmap(texture);
            } else if (loader["tag"] == "progressBG") {
                this.progressBG = new egret.Bitmap(texture);
            } else if (loader["tag"] == "progress") {
                this.progress = new egret.Bitmap(texture);
            }
            this.loadCount--;
            if (this.loadCount == 0) {
                this.addChild(this.logo);
                this.logo.x = this.width / 2 - this.logo.width / 2;
                this.logo.y = this.height / 2 - this.logo.height / 2 - 50;

                this.addChild(this.progressBG);
                this.progressBG.x = this.width / 2 - this.progressBG.width / 2;
                this.progressBG.y = this.height / 2 - this.progressBG.height / 2 + 50;

                this.addChild(this.progress);
                this.progress.fillMode = egret.BitmapFillMode.CLIP;
                this.progress.x = this.width / 2 - this.progress.width / 2;
                this.progress.y = this.height / 2 - this.progress.height / 2 + 50;
                this.progress.width = 0;
                egret.Tween.get(this.progress, {loop: true}).to({alpha: 0.4}, 400).to({alpha: 1}, 400);

                this.progressText.size = 19;
                this.progressText.textColor = 0xC1AA86;
                this.progressText.x = this.width / 2 - this.logo.width / 2 + 3;
                this.progressText.y = this.height / 2 - this.logo.height / 2 + 76;
                this.addChild(this.progressText);
            }
        }
        CommonUtil.loadTextureFromUrl(LoadingUI.logoBase64, loadComplete, this, "logo");
        CommonUtil.loadTextureFromUrl(LoadingUI.progressBGBase64, loadComplete, this, "progressBG");
        CommonUtil.loadTextureFromUrl(LoadingUI.progressBase64, loadComplete, this, "progress");
    }

    public setProgress(current:number, total:number):void {
        if (!this.progress) return;
        this.progress.width = this.progress.texture.textureWidth * (current / total);
        this.progressText.text = Game.getLoadingLanguage("loadingText_"+Game.urlData.language) + Math.ceil((current / total) * 100) + "%";

    }
}
